import 'package:flutter/material.dart';import '../core/theme/colors.dart';class PageIndicator extends StatelessWidget {  const PageIndicator({    required this.currentPage,    required this.pageCount,    super.key,    this.spacing,    this.shape = BoxShape.circle,    this.activeWidth,    this.inactiveWidth,    this.activeHeight,    this.inactiveHeight,    this.activeColor,    this.inactiveColor,    this.borderRadius,    this.hasBorder = false,  });  final int currentPage;  final int pageCount;  final double? spacing;  final double? activeWidth;  final double? inactiveWidth;  final double? activeHeight;  final double? inactiveHeight;  final Color? activeColor;  final Color? inactiveColor;  final BoxShape shape;  final BorderRadiusGeometry? borderRadius;  final bool hasBorder;  @override  Widget build(BuildContext context) {    return Center(      child: Row(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        mainAxisSize: MainAxisSize.min,        children: List.generate(          pageCount,          (index) => AnimatedContainer(            duration: const Duration(milliseconds: 500),            margin: EdgeInsets.only(right: index < pageCount - 1 ? spacing ?? 4 : 0),            width: currentPage == index ? (activeWidth ?? 7) : (inactiveWidth ?? 5),            height: currentPage == index ? (activeHeight ?? 7) : (activeHeight ?? 5),            decoration: BoxDecoration(              shape: shape,              borderRadius: shape != BoxShape.circle ? borderRadius : null,              border:                  hasBorder                      ? Border.all(                        color:                            currentPage == index                                ? (activeColor ?? AppColors.primary)                                : (activeColor?.withValues(alpha: .5) ??                                    AppColors.black.withValues(alpha: .15)),                      )                      : null,              color:                  currentPage == index                      ? (activeColor ?? AppColors.primary)                      : (inactiveColor ?? Color(0xffACDCF5)),            ),          ),        ),      ),    );  }}