import 'package:flutter/material.dart';import 'package:responsive_framework/responsive_framework.dart';class ResponsiveBreakPointWrapper extends StatelessWidget {  const ResponsiveBreakPointWrapper({    required this.child,    required this.firstFrameWidget,    super.key,  });  final Widget child;  final Widget firstFrameWidget;  @override  Widget build(BuildContext context) {    return ResponsiveWrapper.builder(      BouncingScrollWrapper.builder(context, child),      defaultScale: true,      breakpoints: [        ResponsiveBreakpoint.resize(480, name: MOBILE),        ResponsiveBreakpoint.autoScale(800, name: TABLET),        ResponsiveBreakpoint.resize(1000, name: DESKTOP),        ResponsiveBreakpoint.autoScale(2460, name: '4K'),      ],    );  }}