hangeDependencies() {    if (widget.autofocus) {      _focusNode.requestFocus();    }    super.didChangeDependencies();  }  @override  void dispose() {    super.dispose();    (widget.focusNode ?? _focusNode).dispose();    _animationController.dispose();  }  @override  Widget build(BuildContext context) {    return AnimatedBuilder(      animation: _animationController,      builder: (context, child) {        return Tappable(          onTap:              widget.tapAction ??              () {                setState(() {                  if (widget.focusNode != null) {                    widget.focusNode?.requestFocus();                  } else {                    _focusNode.requestFocus();                  }                });              },          child: Column(            crossAxisAlignment: CrossAxisAlignment.stretch,            children: [              if (widget.label != null || widget.labelWidget != null) ...[                widget.labelWidget ??                    Text.rich(                      TextSpan(                        text: widget.label,                        style:                            widget.labelStyle ??                            TextStyles.w400_12(context).copyWith(                              height: 14.62 / 12,                              color: widget.readOnly ? AppColors.black400 : AppColors.black,                            ),                        children: [                          if (!widget.isOptional)                            TextSpan(                              text: ' *',                              style: TextStyles.w800_12(context).copyWith(color: AppColors.error),                            ),                        ],                      ),                    ),                8.vSpace,              ],              SizedBox(                height: widget.height ?? 41.relHeight,                width: widget.width,                child: TextFormField( 